  static sendOrder(email, order, products, total, discounts, original) {
    // Definimos el transporter
    const transporter = nodemailer.createTransport(mg(auth))
    products = products.map(e => (
      {
        ...e,
        price_total: e.price_total.toFixed(2)
      }
    ))
    const template = handlebars.compile(orderDetails)

    const htmlToSend = template({ 
      products: products, 
      discounts: discounts.toFixed(2), 
      total_products: products.length,
      price_original: original.toFixed(2), 
      price_total: total.toFixed(2)
    })
    // Definimos el email
    const mailOptions = {
      from: process.env.ROOT_EMAIL_MAILGUN,
      to: email,
      subject: `¬°Hemos recibido tu pedido N¬∫ ${order}!`,
      html: htmlToSend
    }
    
    transporter.sendMail(mailOptions, (err, info) => {
      if (err) {
        console.log("err", err)
        return false
      }
      console.log('Correo enviado correctamente', info)
      return true
    })
    
    return true
  }

